import { Component, OnInit, ViewChild } from '@angular/core';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { Customer } from 'src/app/model/customer';
import { CustomerService } from 'src/app/service/customer.service';

@Component({
  selector: 'app-customer-list',
  templateUrl: './customer-list.component.html',
  styleUrls: ['./customer-list.component.css']
})
export class CustomerListComponent implements OnInit {
  ELEMENT_DATA: Customer[] = []

  displayedColumns: string[] = ['idCutumer','firstNameCustomer', 'lastNameCustomer', 'cpfCustomer', 'birthdateCustomer', 'statusCustomer', 'emailCustomer'];
  dataSource = new MatTableDataSource<Customer>(this.ELEMENT_DATA);

  @ViewChild(MatPaginator) paginator: MatPaginator;

  constructor(
    private service: CustomerService) { }

  ngOnInit(): void {
    this.findAll();
  }

  findAll() {
    this.service.list().subscribe(resposta => {
      this.ELEMENT_DATA = resposta
      this.dataSource = new MatTableDataSource<Customer>(resposta);
      this.dataSource.paginator = this.paginator;
    })
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
  }

}

<table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

    <!--- Note that these columns can be defined in any order.
          The actual rendered columns are set as a property on the row definition" -->

    <!-- Position Column -->
    <ng-container matColumnDef="idCustomer">
      <th mat-header-cell *matHeaderCellDef> . </th>
      <td mat-cell *matCellDef="let element"> {{element.idCustomer}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="firstNameCustomer">
      <th mat-header-cell *matHeaderCellDef> Primeiro nome </th>
      <td mat-cell *matCellDef="let element"> {{element.firstNameCustomer}} </td>
    </ng-container>

    <!-- Weight Column -->
    <ng-container matColumnDef="lastNameCustomer">
      <th mat-header-cell *matHeaderCellDef> Ultimo nome </th>
      <td mat-cell *matCellDef="let element"> {{element.lastNameCustomer}} </td>
    </ng-container>

    <!-- Symbol Column -->
    <ng-container matColumnDef="cpfCustomer">
      <th mat-header-cell *matHeaderCellDef> Cpf </th>
      <td mat-cell *matCellDef="let element"> {{element.cpfCustomer}} </td>
    </ng-container>

    <ng-container matColumnDef="birthdateCustomer">
      <th mat-header-cell *matHeaderCellDef> Data nasc. </th>
      <td mat-cell *matCellDef="let element"> {{element.birthdateCustomer}} </td>
    </ng-container>

    <ng-container matColumnDef="cpfCustomer">
      <th mat-header-cell *matHeaderCellDef> Status </th>
      <td mat-cell *matCellDef="let element"> {{element.cpfCustomer}} </td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>
