import { Component, OnInit, ViewChild } from '@angular/core';
import { CategoryService } from '../service/category.service';
import { Category } from '../model/category';
import { DatePipe } from '@angular/common';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { ToastrService } from 'ngx-toastr';
import { NgModule } from '@angular/core';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html', 
  styleUrls: ['./category.component.css']
})

export class CategoryComponent implements OnInit {

  success: boolean = false;
  errors!: String[];

  ELEMENT_DATA: Category[] = [];


  displayedColumns: string[] = ['idCategory', 'nameCategory', 'descriptionCategory', 'deleteCategory', 'findCategory'];
  dataSource = new MatTableDataSource<Category>(this.ELEMENT_DATA)



  @ViewChild(MatPaginator) paginator!: MatPaginator;
  CategoryService: any;
  toastr: any;
  idCategory: any;
  message: string | undefined;


  constructor(
    private service: CategoryService) { }

  ngOnInit(): void {
    this.listCategory();

  }

  category: Category = {
    idCategory: '',
    nameCategory: '',
    descriptionCategory: ''
  }

  saveCategory() {

    this.service.save(this.category).subscribe({next: response => {
      this.success = true;
      this.errors = [];
      this.listCategory();
    //this.toast.success('A categoria '+ this.category.nameCategory +' foi cadastrada com sucesso!', 'Sucesso!!!');      
    }, error: ex => {
      if (ex.error.errors) {
        this.errors = ex.error.errors;
        this.success = false;
        ex.error.errors.forEach((element:any) => {
          //this.toast.error(element.message, 'Atenção!!!');                    
        });
      } else {
          this.success = false;
          this.errors = ex.error.errors;
        //this.toast.error(ex.error.message, 'Atenção!');
      }
    }})

  }

  listCategory() {
    this.service.list().subscribe((response:any) => {
      this.ELEMENT_DATA = response.result as Category[];
      this.dataSource = new MatTableDataSource<Category>(this.ELEMENT_DATA);
      this.dataSource.paginator = this.paginator;
    })
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
  }

deleteCategory(category :Category) {
 if(window.confirm('Deseja excluir?')){

this.service.delete(category.idCategory).subscribe((response:any) => {
this.message = response.result.result as string;
window.alert(this.message);
this.listCategory();
 }   
  );
 }

}

findCategory(category:Category){
  this.service.findById(category.idCategory).subscribe((response:any) => { 
    this.category = response.result as Category;

  });
}

}
<mat-toolbar>
    <span>
        <mat-icon>category</mat-icon>
        Categorias
    </span>
</mat-toolbar>
<mat-tab-group #tabGroup="matTabGroup" class="full-width">
    <mat-tab>
        <ng-template mat-tab-label>
            <mat-icon>note_add</mat-icon>
            Cadastrar Categoria
        </ng-template>
        <mat-card class="full-width">
            <mat-card-title>Novo Cadastro</mat-card-title>
            <form>
                <div>
                    <mat-form-field class="full-width">
                        <mat-label> Nome: </mat-label>
                        <input [(ngModel)]="category.nameCategory" type="text" matInput name="nameCategory"
                            placeholder="Digite o nome da categoria" required />
                    </mat-form-field>
                    <mat-form-field class="full-width">
                        <mat-label>Descrição: </mat-label>
                        <input [(ngModel)]="category.descriptionCategory" type="text" matInput name="descriptionCategory"
                            placeholder="Descreva a categoria" required />
                    </mat-form-field>

                    <button (click)="saveCategory()" mat-raised-button color="primary">Salvar</button>
                </div>
                <br />
                <div role="alert" *ngIf="success == true">
                    <br>
                    <h1> Categoria {{ category.nameCategory }} cadastrado com sucesso! <mat-icon>done_all</mat-icon>
                    </h1>
                </div>
                <div role="alert" *ngFor="let error of errors">
                    <span>{{ error }}</span>
                </div>
            </form>
        </mat-card>
    </mat-tab>
    <mat-tab>
        <ng-template mat-tab-label>
            <mat-icon>filter_list</mat-icon>
            Listar Categoria
        </ng-template>
        <mat-card class="full-width">

            <table mat-table [dataSource]="dataSource" class="full-width" matTableResponsive>

                <ng-container matColumnDef="idCategory">
                    <th mat-header-cell *matHeaderCellDef> ID </th>
                    <td mat-cell *matCellDef="let category"> {{ category.idCategory }} </td>
                </ng-container>

                <ng-container matColumnDef="nameCategory">
                    <th mat-header-cell *matHeaderCellDef> Nome da Categoria </th>
                    <td mat-cell *matCellDef="let category"> {{ category.nameCategory }} </td>
                </ng-container>

                <ng-container matColumnDef="descriptionCategory">
                    <th mat-header-cell *matHeaderCellDef> Descrição da Categoria </th>
                    <td mat-cell *matCellDef="let category"> {{ category.descriptionCategory }} </td>
                </ng-container>

                <ng-container matColumnDef="deleteCategory">
                    <th mat-header-cell *matHeaderCellDef> Excluir </th>
                    <td mat-cell *matCellDef="let category"> 
                 <a href = "#" routerLink="category" (click)="deleteCategory(category)"><mat-icon> delete</mat-icon></a>
                </td>
                </ng-container>

                <ng-container matColumnDef="findCategory">
                    <th mat-header-cell *matHeaderCellDef> Editar </th>
                    <td mat-cell *matCellDef="let category"> 
                 <a href = "#" routerLink="category" (click)="tabGroup.selectedIndex=0;findCategory(category)"><mat-icon> edit</mat-icon></a>
                </td>
                </ng-container>


                <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
                <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table>

            <mat-paginator [pageSizeOptions]="[5, 10, 20, 50]" showFirstLastButtons></mat-paginator>


        </mat-card>
    </mat-tab>
</mat-tab-group>
